    /// # #[cfg(not(feature = "nightly"))]
    /// # use opaque_allocator_api::alloc::Global;
    /// #
    /// let proj_map = TypedProjIndexMap::from([
    ///     (0_usize,  0_isize),
    ///     (1_usize,  1_isize), (2_usize, 1_isize), (3_usize, 1_isize), (4_usize, 1_isize),
    ///     (5_usize,  2_isize),
    ///     (6_usize,  3_isize),
    ///     (7_usize,  5_isize),
    ///     (8_usize,  8_isize),
    ///     (9_usize,  13_isize),
    ///     (10_usize, 21_isize),
    ///     (11_usize, 34_isize),
    ///     (12_usize, 55_isize),
    /// ]);
