    /// assert_eq!(proj_map.binary_search_by_key(&13, |&a, &b| b),  Ok(9));
    /// assert_eq!(proj_map.binary_search_by_key(&4, |&a, &b| b),   Err(7));
    /// assert_eq!(proj_map.binary_search_by_key(&100, |&a, &b| b), Err(13));
